import React, { useEffect, useRef, useState } from "react";
import ChatBubble from "./ChatBubble";
import { Message } from "../utils";
import Loader from "./Loader";

interface Props {
  messages: Message[];
  msg: string | null;
  reply: string | null;
  isFetching: boolean;
}
function ChatContainer({ messages, msg, reply, isFetching }: Props) {
  const div = useRef<HTMLDivElement>(null);

  useEffect(() => {
    div.current?.scrollIntoView({ behavior: "smooth" });
  }, [msg, reply]);

  return (
    <div className="flex">
      {messages.length > 0 ? (
        <div className="flex flex-1 p-5 overflow-y-auto flex-col">
          {messages.map((m, index) => (
            <ChatBubble key={index} text={m.message} isUser={m.isUser} />
          ))}
          {isFetching && (
            <div
              className={`${"bg-orange-50 self-start"} text-clip p-5 mb-5 rounded-lg whitespace-pre-line`}
            >
              <Loader />
            </div>
          )}
        </div>
      ) : (
        <div className="flex flex-1">
          Enter ingredients and let LlaMa Lunch generate a recipe for you!
        </div>
      )}
    </div>
  );
}

export default ChatContainer;
