import React, { useEffect, useRef, useState } from "react";
import ChatBubble from "./ChatBubble";
import { Message } from "../utils";

interface Props {
  reply: string;
  msg: string | null;
}
function ChatContainer({ reply, msg }: Props) {
  const [msgs, setMsgs] = useState<Message[]>([]);
  const div = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const stringifymsgs = localStorage.getItem("messages");
    if (stringifymsgs) {
      const messages = JSON.parse(stringifymsgs);
      console.log(messages);
      setMsgs(messages);
      div.current.scrollIntoView({ behavior: "smooth", block: "end" });
    }
  }, [msg]);

  return (
    <div className="flex flex-1 p-5 overflow-y-auto flex-col" ref={div}>
      {/* {msg && <ChatBubble text={msg} isUser={true} />}
      {reply && <ChatBubble text={reply} isUser={false} />} */}
      {msgs.slice(0, -2).map((m, index) => (
        <ChatBubble key={index} text={m.message} isUser={m.isUser} />
      ))}
      {msg && <ChatBubble text={msg} isUser={true} />}
      {msg && !reply && <div>...</div>}
      {msg && reply && <ChatBubble text={reply} isUser={false} />}
    </div>
  );
}

export default ChatContainer;
