import React, { SyntheticEvent, useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
import TextInput from "./components/TextInput";
import ChatContainer from "./components/ChatContainer";
import cook from "./assets/cook.png";
import { Message, getReplyFromQn } from "./utils";
import ChatBubble from "./components/ChatBubble";
function App() {
  const [isSent, setIsSent] = useState(false);
  const [currMsg, setCurrMsg] = useState<string | null>(null);
  const [reply, setReply] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [refetch, setRefetch] = useState(false);

  useEffect(() => {
    // load in messages from db
    const localMsgsString = localStorage.getItem("messages");
    let loadedMsgs = [];
    if (localMsgsString) {
      loadedMsgs = JSON.parse(localMsgsString);
    }
    setMessages(loadedMsgs);
  }, []);

  useEffect(() => {
    if (currMsg) {
      const temp = messages;
      temp.push({
        isUser: true,
        message: currMsg,
      });
      setMessages(temp);
    }
  }, [isSent]);
  useEffect(() => {
    const send = async () => {
      if (isSent && currMsg) {
        // const temp = messages;
        // temp.push({
        //   isUser: true,
        //   message: currMsg,
        // });
        console.log("fetching");
        const res = await getReplyFromQn(currMsg);
        temp.push({
          isUser: false,
          message: res,
        });
        setMessages(temp);
      }
      setIsSent(false);
    };
    send();
  }, [isSent]);

  useEffect(() => {
    if (refetch) {
      setMessages([]);
      setCurrMsg(null);
      setReply(null);
      setRefetch(false);
    }
  }, [refetch]);

  return (
    <div className="w-screen h-screen flex justify-center">
      <div className="pt-3 pb-3 pl-6 pr-6 w-full h-full flex flex-col">
        <div className="text-xl font-bold h-[40px]">LlaMa Lunch</div>
        {/* chat container */}
        {/* <ChatContainer reply={reply} msg={currMsg} messages={messages} /> */}
        <div className="flex flex-1 p-5 overflow-y-auto flex-col">
          {messages.map((m, index) => (
            <ChatBubble key={index} text={m.message} isUser={m.isUser} />
          ))}
        </div>
        {/* text input */}
        <TextInput
          setRefetch={setRefetch}
          setIsSent={setIsSent}
          setCurrMsg={setCurrMsg}
          currMsg={currMsg}
        />
        {/* footer */}
        <div className="p-3 text-sm text-slate-500">
          Made with{" "}
          <a
            href="https://ai.meta.com/llama/"
            className="font-semibold"
            target="_blank"
            rel="noreferrer"
          >
            Llama2
          </a>
          , by Andre Lin, Bernice Toh, Kang Yue Ran
        </div>
      </div>
    </div>
  );
}

export default App;
