import React, { useEffect, useRef, useState } from "react";
import ChatBubble from "./ChatBubble";
import { Message } from "../utils";

interface Props {
  messages: Message[];
  msg: string | null;
  reply: string | null;
}
function ChatContainer({ messages, msg, reply }: Props) {
  const div = useRef<HTMLDivElement>(null);

  useEffect(() => {
    div.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="flex flex-1 p-5 overflow-y-auto flex-col">
      {messages.map((m, index) => (
        <ChatBubble
          key={index}
          text={m.message}
          isUser={m.isUser}
          ref={index === messages.length - 1 ? div : null}
        />
      ))}
    </div>
  );
}

export default ChatContainer;
