import React, {
  Dispatch,
  SetStateAction,
  SyntheticEvent,
  useState,
} from "react";
import cook from "../assets/cook.png";
import axios from "axios";
import { VscDebugRestart } from "react-icons/vsc";
import { Message } from "../utils";
interface Props {
  msg: string | null;
  reply: string;
  setMsg: Dispatch<SetStateAction<string | null>>;
  setIsSent: Dispatch<SetStateAction<boolean>>;
}
function TextInput({ msg, setMsg, setIsSent, reply }: Props) {
  const [thisMsg, setThisMsg] = useState<Message>({
    message: "",
    isUser: true,
  });
  const handleSubmit = (e: SyntheticEvent) => {
    e.preventDefault();
    // if prev msg exists, store in localstorage
    if (msg) {
      let messages = localStorage.getItem("messages");
      let temp = [];
      if (messages) {
        temp = JSON.parse(messages);
      }
      temp.push({});
      localStorage.setItem("messages", JSON.stringify(temp));
    }
    // then set msg to this msg
    setMsg(thisMsg);
    setIsSent(true);
    setThisMsg("");
  };

  const clear = () => {
    localStorage.removeItem("messages");
    console.log("cleared!");
  };
  return (
    <div className="w-full flex">
      <form
        onSubmit={handleSubmit}
        className="w-full flex justify-between gap-[15px]"
      >
        <input
          className="flex flex-1 p-3 bg-slate-100 rounded-lg outline-black"
          placeholder="Enter ingredients"
          value={thisMsg}
          onChange={(e) => setThisMsg(e.target.value)}
        />
        <button
          type="submit"
          className="bg-black rounded-full p-2 w-[50px] h-[50px]"
          disabled={thisMsg.length === 0}
        >
          {" "}
          <img src={cook} className="brightness-0 invert" alt="cook" />
        </button>
        <button
          type="button"
          onClick={clear}
          className="bg-black rounded-full p-2 w-[50px] h-[50px]"
        >
          {" "}
          {/* <VscDebugRestart />{" "} */}restart
        </button>
      </form>
    </div>
  );
}

export default TextInput;
