import React from "react";
import ChatBubble from "./ChatBubble";

interface Props {
  reply: string;
  msg: string;
  isSent: boolean;
}
function ChatContainer({ reply, msg, isSent }: Props) {
  return (
    <div className="flex flex-1 p-5 overflow-y-auto">
      {isSent && <ChatBubble text={msg} isUser={true} />}
      {reply && <div className="whitespace-pre-line">{reply}</div>}
    </div>
  );
}

export default ChatContainer;
