import React from "react";
interface Props {
  text: string;
  isUser: boolean;
  ref?: HTMLDivElement;
}
function ChatBubble({ text, isUser, ref }: Props) {
  return (
    <div>
      <div
        className={`${
          isUser ? "bg-blue-50 self-end" : "bg-orange-50 self-start"
        } text-clip p-5 mb-5 rounded-lg whitespace-pre-line max-w-xl`}
      >
        {text}
      </div>
    </div>
  );
}

export default ChatBubble;
