import React, { SyntheticEvent, useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
import TextInput from "./components/TextInput";
import ChatContainer from "./components/ChatContainer";
import cook from "./assets/cook.png";
import { Message, getReplyFromQn } from "./utils";
function App() {
  const [msg, setMsg] = useState<string | null>(null);
  const [isChanged, setIsChanged] = useState<boolean>(false);
  const [reply, setReply] = useState<string>("");
  const [msgs, setMsgs] = useState<string[]>();

  useEffect(() => {
    const getReply = async () => {
      try {
        if (msg) {
          console.log("getting");
          const data = await getReplyFromQn(msg);
          const reply: Message = {
            isUser: false,
            message: data,
          };
          const newMsg: Message = {
            isUser: true,
            message: msg,
          };
          let tempMsgs: Message[] = [];
          const messages = localStorage.getItem("messages");

          if (messages) {
            tempMsgs = JSON.parse(messages);
          }
          tempMsgs.push(newMsg);
          tempMsgs.push(reply);
          localStorage.setItem("messages", JSON.stringify(tempMsgs));
          setReply(data);
          setMsg(null);
        }
      } catch (e) {
        console.log(e);
        setIsSent(false);
      }
    };
    getReply();
  }, [msg]);

  return (
    <div className="w-screen h-screen flex justify-center">
      <div className="pt-3 pb-3 pl-6 pr-6 w-full h-full flex flex-col">
        <div className="text-xl font-bold h-[40px]">LlaMa Lunch</div>
        {/* chat container */}
        <ChatContainer reply={reply} msg={msg} />
        {/* text input */}
        <TextInput msg={msg} setMsg={setMsg} setIsSent={setIsChanged} />
        {/* footer */}
        <div className="p-3 text-sm text-slate-500">
          Made with{" "}
          <a
            href="https://ai.meta.com/llama/"
            className="font-semibold"
            target="_blank"
            rel="noreferrer"
          >
            Llama2
          </a>
          , by Andre Lin, Bernice Toh, Kang Yue Ran
        </div>
      </div>
    </div>
  );
}

export default App;
