import axios from "axios";

export const getReplyFromQn = async (qn: string) => {
  const res = await axios.get("http://127.0.0.1:5000/api", {
    params: {
      prompt: qn,
    },
  });
  console.log(res.data);
  return res.data;
};

export const saveMessages = (messages: Message[]) => {
  localStorage.setItem("messages", JSON.stringify(messages));
};

export interface Message {
  message: string;
  isUser: boolean;
}
export interface Recipe {
  opening: string;
  title: string;
  ingredients: string;
  duration: string;
  steps: string;
  ending: string;
}

export const getRecipe = (recipeString: string) => {
  const sections = recipeString.split(/\n(?=\w+:)/);
  const recipe = {
    opening: sections[0].trim(),
    title: sections[1].trim(),
    ingredients: sections[2].trim(),
    duration: sections[3].trim(),
    steps: sections.slice(4, sections.length - 1).join("\n"), // Combine the steps section
    ending: sections[sections.length - 1].trim(),
  };
};
