import React, { SyntheticEvent, useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
import TextInput from "./components/TextInput";
import ChatContainer from "./components/ChatContainer";
import cook from "./assets/cook.png";
function App() {
  const [msg, setMsg] = useState<string>("");
  const [isSent, setIsSent] = useState<boolean>(false);
  const [reply, setReply] = useState<string>("");

  useEffect(() => {
    if (isSent) {
      const getReply = async (e: SyntheticEvent) => {
        e.preventDefault();
        console.log("getting");
        const data = await axios.get("http://127.0.0.1:5000/api");
        console.log(data);
      };
    }
  }, [isSent]);
  return (
    <div className="w-screen h-screen flex justify-center">
      <div className="p-10 w-full h-full flex flex-col">
        <div className="text-xl font-bold h-[40px]">LlaMa Lunch</div>
        {/* chat container */}
        <ChatContainer />
        {/* text input */}
        <TextInput msg={msg} setMsg={setMsg} setIsSent={setIsSent} />
        {/* footer */}
        <div className="p-5 text-sm text-slate-500">
          Made with{" "}
          <a
            href="https://ai.meta.com/llama/"
            className="font-semibold"
            target="_blank"
            rel="noreferrer"
          >
            Llama2
          </a>
          , by Andre Lin, Bernice Toh, Kang Yue Ran
        </div>
      </div>
    </div>
  );
}

export default App;
