import React from "react";
import ChatBubble from "./ChatBubble";

interface Props {
  reply: string;
  msg: string | null;
}
function ChatContainer({ reply, msg }: Props) {
  return (
    <div className="flex flex-1 p-5 overflow-y-auto">
      {msg && <ChatBubble text={msg} isUser={true} />}
      {reply && <div className="whitespace-pre-line">{reply}</div>}
      {msg && !reply && <div>...</div>}
    </div>
  );
}

export default ChatContainer;
