import React, {
  Dispatch,
  SetStateAction,
  SyntheticEvent,
  useState,
} from "react";
import cook from "../assets/cook.png";
import axios from "axios";
import { VscDebugRestart } from "react-icons/vsc";
import { Message } from "../utils";
interface Props {
  setIsSent: Dispatch<SetStateAction<boolean>>;
  setCurrMsg: Dispatch<SetStateAction<string | null>>;
  currMsg: string | null;
  setRefetch: Dispatch<SetStateAction<boolean>>;
  setMessages: Dispatch<SetStateAction<Message[]>>;
  messages: Message[];
}
function TextInput({
  setCurrMsg,
  setIsSent,
  currMsg,
  setRefetch,
  setMessages,
  messages,
}: Props) {
  const [thisMsg, setThisMsg] = useState("");
  const clear = () => {
    setRefetch(true);
    localStorage.removeItem("messages");
    console.log("cleared!");
  };

  const handleSubmit = () => {
    saveMessages();
    setCurrMsg(thisMsg);
    const temp = messages;
    temp.push({ isUser: true, message: thisMsg });
    setMessages(temp);
    setIsSent(true);
    setThisMsg("");
  };

  const saveMessages = () => {
    localStorage.setItem("messages", JSON.stringify(messages));
  };

  return (
    <div className="w-full flex">
      <div className="w-full flex justify-between gap-[15px]">
        <input
          className="flex flex-1 p-3 bg-slate-100 rounded-lg outline-black"
          placeholder="Enter ingredients"
          value={thisMsg}
          onChange={(e) => setThisMsg(e.target.value)}
        />
        <button
          className="bg-black rounded-full p-2 w-[50px] h-[50px]"
          disabled={thisMsg.length === 0}
          onClick={handleSubmit}
        >
          {" "}
          <img src={cook} className="brightness-0 invert" alt="cook" />
        </button>
        <button
          type="button"
          onClick={clear}
          className="bg-black rounded-full p-2 w-[50px] h-[50px]"
        >
          {" "}
          restart
        </button>
      </div>
    </div>
  );
}

export default TextInput;
