import React from "react";
import llama from "../assets/llama.png";
interface Props {
  text: string;
  isUser: boolean;
  ref?: HTMLDivElement;
}
function ChatBubble({ text, isUser, ref }: Props) {
  return (
    <div>
      {!isUser && (
        <div className="rounded-full w-10 h-10 bg-slate-200">
          <img className="invert brightness-0" alt="llama" src={llama} />
        </div>
      )}
      <div
        className={`${
          isUser ? "bg-blue-50 self-end" : "bg-orange-50 self-start"
        } text-clip p-5 mb-5 rounded-lg whitespace-pre-line max-w-xl`}
      >
        {text}
      </div>
    </div>
  );
}

export default ChatBubble;
